import React, { useState, useEffect } from 'react';

const ImageLazyLoader = ({ src, alt, style, thumbnailSrc }) => {
console.log(src);
    thumbnailSrc = thumbnailSrc || '';

    const [imageSrc, setImageSrc] = useState(thumbnailSrc);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach((entry) => {
                if (entry.isIntersecting) {
                    setIsVisible(true);
                    observer.unobserve(entry.target);
                }
            });
        });

        observer.observe(document.querySelector(`#image-${alt}`));

        return () => {
            observer.disconnect();
        };
    }, [alt]);

    function convertCssToJsxStyle(cssString) {
        const rules = cssString.split(';');
      
        const styleObject = {};
      
        for (const rule of rules) {
          const trimmedRule = rule.trim();
      
          if (!trimmedRule || !trimmedRule.includes(':')) {
            continue;
          }
      
          const [key, value] = trimmedRule.split(':');
      
          const camelCaseKey = key.replace(/-([a-z])/g, (match, group1) => group1.toUpperCase());
      
          styleObject[camelCaseKey] = value.trim();
        }   
        console.log(styleObject);
      
        return styleObject;
      }

    useEffect(() => {
        if (isVisible) {
            const image = new Image();
            image.src = src;
            image.onload = () => {
                setImageSrc(src);
            };
        }
    }, [isVisible, src]);

    return (
        <img
            style={convertCssToJsxStyle(style)}
            id={`image-${alt}`}
            src={imageSrc}
            alt={alt}
        />
    );
};

export default ImageLazyLoader;